import React from "react";
import Contato from "./Assets/Components/Contato";
import Header from "./Assets/Components/Header";
import Main from "./Assets/Components/Main";
import Portfolio from "./Assets/Components/Portfolio";
import Servicos from "./Assets/Components/Servicos";
import Sobre from "./Assets/Components/Sobre";

import "./Assets/css/style.css";

function App() {
  const sections = document.querySelectorAll('[ data-anime="scroll"]');

  function handleScroll() {
    if (sections.length) {
      const windowMetade = window.innerHeight * 0.5;

      function animaScroll() {
        sections.forEach((section) => {
          const sectionTop = section.getBoundingClientRect().top;
          const isVisible = sectionTop - windowMetade < 0;
          if (isVisible) {
            section.classList.add("ativo");
          } else if (section.classList.contains("ativo")) {
            section.classList.remove("ativo");
          }
        });
      }

      window.addEventListener("scroll", animaScroll);
    }
  }

  return (
    <>
      <Header />
      <Main onScroll={handleScroll} />
      <Sobre onScroll={handleScroll} />
      <Servicos onScroll={handleScroll} />
      <Portfolio onScroll={handleScroll} />
      <Contato onScroll={handleScroll} />
    </>
  );
}

export default App;
